function extractCoordinates(){if(navigator.geolocation)navigator.geolocation.getCurrentPosition(coords);else{var o=document.getElementById("weather");o.innerHTML="Geolocation is not supported by this browser."}}function coords(o){var t={latitude:o.coords.latitude,longitude:o.coords.longitude};postCoords(t)}function postCoords(o){$.ajax({url:"/coords",type:"POST",data:o,success:function(){console.log(o)},error:function(o){console.error(o)}})}$(document).ready(function(){extractCoordinates()});