function extractCoordinates(){if(navigator.geolocation)navigator.geolocation.getCurrentPosition(coords);else{var o=document.getElementById("weather");o.innerHTML="Geolocation is not supported by this browser."}}function coords(o){coordinates={latitude:o.coords.latitude,longitude:o.coords.longitude},$.ajax({url:"/coords",type:"POST",data:coordinates,success:function(){console.log(coordinates)},error:function(o){console.error(o)}})}var coordinates;$(document).ready(function(){extractCoordinates()});